mapper.select:
SELECT * FROM (
    SELECT rownum rnum, B.* FROM (



        	AND mCate=#{category}

        ORDER BY mNo DESC
    ) B
) R
WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE market 
set mTitle=#{mTitle}, mContent=#{mContent}, mCate=#{mCate}, mPrice=#{mPrice}
where mNo=#{mNo}
